import React, {Component} from 'react'
import { NavLink } from 'react-router-dom'
import LoginService from '../services/LoginService';

class allDonor extends Component{
    constructor(props){
        super(props)
        this.state={
            donor: []
        }
        //this.editDonor = this.editDonor.bind(this);
        //this.deleteDonor = this.deleteDonor.bind(this);
    }
    // editDonor(id){
    //     this.props.history.push(`/updateDonor/${id}`);
    // }
    componentDidMount(){
         LoginService.getallDonor().then((res) => {
             this.setState({ donor:res.data});
         });
     }
    render(){
        return(
            <div>
                <br></br>
                <ul className="header"> 
                <li><NavLink exact to="/Donor">Donor</NavLink></li>
                <li><NavLink to ="/search">Search Donor</NavLink></li>
                <li><NavLink to ="/Stock">Stock</NavLink></li>
                <li><NavLink to ="/DeleteDonor">Delete Donor</NavLink></li>
                <li><NavLink to ="/exit">Exit</NavLink></li>
                </ul>
                <h2 className="text-center"> Donor List</h2>
                    <div className="row">
                    <table className="table table-striped table-borderd">
                        <thead>
                        <tr>
                                <th> First Name</th>
                                <th> Last Name</th>
                                <th> DOB</th>
                                <th> Gender</th>
                                <th> Blood Group</th>
                                <th> Email Id</th>
                                <th> Contact No</th>
                                <th>Complete Address</th>
                                <th> City</th>
                                <th> State</th>
                                <th> Pincode</th>
                                <th>Actions</th>
                            </tr>
                        <tbody>
                            {
                                this.state.donor.map(
                                    donor=>
                                    <tr key= {donor.id}>
                                       <td> {donor.firstName}</td>
                                        <td> {donor.lastName}</td>
                                        <td> {donor.dateOfBirth}</td>
                                        <td> {donor.gender}</td>
                                        <td> {donor.bloodGroup}</td>
                                        <td> {donor.emailId}</td>
                                        <td> {donor.contactNumber}</td>
                                        <td> {donor.address}</td>
                                        <td> {donor.city}</td>
                                        <td> {donor.state}</td>
                                        <td> {donor.pincode}</td>
                                        <td>
                                                 <button onClick={ () => this.editDonor(donor.id)} className="btn btn-info">Update </button>
                                                 <button style={{marginLeft: "10px"}} onClick={ () => this.deleteEmployee(donor.id)} className="btn btn-danger">Delete </button>
                                                 <button style={{marginLeft: "10px"}} onClick={ () => this.viewEmployee(donor.id)} className="btn btn-info">View </button>
                                        </td>
                                    </tr>
                                 )
                                }
                        </tbody>
                        </thead>
                    </table>
                </div>
            </div>
        )
    }
}
export default allDonor
