import React, { Component } from 'react'
import { NavLink } from 'react-router-dom';
class Search extends Component{
  constructor(props) {
    super(props)

    this.state = {
        
        BloodGroup:'',
        BarStyling: {width:"20rem",background:"#F2F1F9", border:"none", padding:"0.3rem"},
        BarStyling2: {width:"20rem",background:"#F2F1F9", border:"none", padding:"0.3rem"},
        keyword:'',
        setKeyword:''
    }
  }

// const Search = ({keyword,setKeyword}) => 
// {
// const BarStyling = {width:"20rem",background:"#F2F1F9", border:"none", padding:"0.5rem"};
  
render() {
  return (
      <div>
      <br></br>
      <ul className="header"> 
          
          <li><NavLink exact to="/Donor">Donor</NavLink></li>
          <li><NavLink to ="/search">Search Donor</NavLink></li>
          <li><NavLink to ="/Stock">Stock</NavLink></li>
          <li><NavLink to ="/DeleteDonor">Delete Donor</NavLink></li>
          <li><NavLink to ="/exit">Exit</NavLink></li>

         </ul> 
         <div className = "container">
            <div className = "row">
                <div className = "card col-md-6 offset-md-3 offset-md-3">
                    <h2 className="text-center">Search Donor</h2>
                            <div className="card-body" >
                              <div className="text-center">
                                <div>
                              <select BloodGroup={this.state.BloodGroup} style={this.state.BarStyling2} onChange={this.handleChange} >
                            
                                        <option BloodGroup="--" >Select Blood Group </option>  
                                        <option BloodGroup="A+" >A+ </option>  
                                        <option BloodGroup="B+"> B+ </option>  
                                        <option BloodGroup="AB+"> AB+ </option>
                                        <option BloodGroup="O+"> O+ </option>
                                        <option BloodGroup="A-"> A- </option>
                                        <option BloodGroup="B-"> B- </option>
                                        <option BloodGroup="AB-"> AB- </option>  
                                        <option BloodGroup="O-"> O- </option>
                                        </select>
                                        </div>
                                        <br></br>
                                        <div>
                                    <input 
                                      style={this.state.BarStyling}
                                      key="random1"
                                      value={this.state.keyword}
                                      placeholder={"Enter Location/City"}
                                      onChange={(e) => this.state.setKeyword(e.target.value)}
                                    />
                                    </div>
                                    </div>
                            </div> 
                            {/* <div class="form-check form-check-inline">Search BY:<br></br>     
                                <input style={{marginLeft: "2px"}} class="form-check-input" type="checkbox" id="inlineCheckbox1" value="option1"></input>
                                <label class="form-check-label" for="inlineCheckbox1">Location</label>
                            
                            <div class="form-check form-check-inline">
                                <input style={{marginLeft: "2px"}} class="form-check-input" type="checkbox" id="inlineCheckbox2" value="option2"></input>
                                <label class="form-check-label" for="inlineCheckbox2">Blood Group</label>
                            </div> 
                            </div>                  */}
                </div>
              </div>
            </div>
          </div>
);
  }
}
export default Search




