import React, { Component } from "react";
import { NavLink } from "react-router-dom";
// import axios from 'axios'

 import LoginService  from "../services/LoginService";


 class LoginComponent extends Component {
    
  constructor(props) {
    super(props)

    this.state = {
        id: this.props.match.params.id,
        username: '',
        password: '',
        role:'',
    }

    this.changeUserNameHandler = this.changeUserNameHandler.bind(this)
    this.changePasswordHandler = this.changePasswordHandler.bind(this)
    this.handleChange = this.handleChange.bind(this)
    this.loginClicked = this.loginClicked.bind(this)
    
}

changeUserNameHandler= (event) => {
  this.setState({username: event.target.value});
}

changePasswordHandler= (event) => {
  this.setState({password: event.target.value});
}

handleChange= (event) => {
  this.setState({role: event.target.value});
}

loginClicked() {
LoginService.adminLogin(this.state.username,this.state.password,this.state.role).then(resp => {
  console.log(resp.data);
  if(resp.status === 200 && resp.data.role=== "admin"){
    this.props.history.push('/donor');
  }
  else if(resp.status === 200 && resp.data.role==="user" ){
    this.props.history.push('/user');

  }

  // else if(resp.status === 404 ){
  //   alert("Invalid Credentials");
  //   return;
  // }
  // if(resp.data.obj.status === 1) {
  //   openFirstPage();
  // } else {
  //   errorMessage();
  // }
})
}
    render() {
      
        return (
          <div className = "container">
                        <div className = "row">
                            <div className = "card col-md-12 offset-md- offset-md-">
                                {/* <h2 className="text-center">Add Donor</h2> */}
                                    <div className="card-body"></div>
        <div className="base-container" ref={this.props.containerRef}>
          <div>
            <ul className="header">
                <li><NavLink exact to="/">Home</NavLink></li>
                <li><NavLink to="/stuff">About</NavLink></li>
                <li><NavLink to="/contact">Contact</NavLink></li>
                <li><NavLink to="/Login">Login</NavLink></li>
            </ul> 
          </div>
          <label>
          Login As:
          <select role={this.state.role} onChange={this.handleChange} >
              <option value="select" >SELECT ONE </option>  
              <option value="admin"> ADMIN </option>  
              <option value="user"> USER </option>  
              
           </select>
        </label>

        <div className="content">
            
          <div className="form">
            <div className="form-group">
              <label htmlFor="username">Username : </label>
              <input type="text" name="username" placeholder="Username" 
              value={this.state.username} onChange={this.changeUserNameHandler}/>
            </div>
            
            <div className="form-group">
              <label htmlFor="password">Password: </label>
              <input type="password" name="password" placeholder="Password" 
              value={this.state.password} onChange={this.changePasswordHandler}/>
            </div>
          </div>
        </div>
        <div className="footer">
          <button type="button" className="btn" onClick={this.loginClicked}>
            Login
          </button>
        </div>
        </div>
        </div>
        </div>
        </div>
    );
    }
}
