import React, { Component } from "react";
import { NavLink } from "react-router-dom";
class Stock extends Component {
  constructor(props) {
    super(props)

    this.state = {
        
        BloodGroup:''
    }
  }
    
      IncrementItem = () => {
          this.setState(prevState => {
            if(prevState.quantityAPostive < 9) {
              return {
                quantityAPostive: prevState.quantityAPostive + 1
              }
            } else {
              return null;
            }
          });
      }
      DecreaseItem = () => {
        this.setState(prevState => {
          if(prevState.quantityAPostive > 0) {
            return {
                quantityAPostive: prevState.quantityAPostive - 1
            }
          } else {
            return null;
          }
        });
      }
      ToggleClick = () => {
        this.setState({
          show: !this.state.show
        });
      }
      handleChange= (event) => {
        this.setState({BloodGroup: event.target.value});
      }
      
    
      render() {
        return (<div>
          <ul className="header"> 
          
          <li><NavLink exact to="/Donor">Donor</NavLink></li>
          <li><NavLink to ="/search">Search Donor</NavLink></li>
          <li><NavLink to ="/Stock">Stock</NavLink></li>
          <li><NavLink to ="/DeleteDonor">Delete Donor</NavLink></li>
          <li><NavLink to ="/">Exit</NavLink></li>
          </ul>
            <div className = "container">
                <div className = "row">
                    <div className = "card col-md-6 offset-md-3 offset-md-3">
                        <h2 className="text-center">Adjust Stock</h2>
                        <div className="card-body">
                            <form>
                            <div className = "form-group">
                           
                                    <label> BloodG Group: </label>
                                    <select role={this.state.BloodGroup} onChange={this.handleChange} >
                                              <option BloodGroup="A+" >A+ </option>  
                                              <option BloodGroup="B+"> B+ </option>  
                                              <option BloodGroup="AB+"> AB+ </option>
                                              <option BloodGroup="O+"> O+ </option>
                                              <option BloodGroup="A-"> A- </option>
                                              <option BloodGroup="B-"> B- </option>
                                              <option BloodGroup="AB-"> AB- </option>  
                                              <option BloodGroup="O-"> O- </option>
                                              </select>
                                              <input placeholder="Update Blood Stock" BloodGroup="BloodGroup" className="form-control" 
                                              value={this.state.BloodGroup} onChange={this.changeBloodGroupHandler}/>
                               
                            {/* <label>
          Blood Group:</label>
          
          <select role={this.state.BloodGroup} onChange={this.handleChange} >
              <option BloodGroup="A+" >A+ </option>  
              <option BloodGroup="B+"> B+ </option>  
              <option BloodGroup="AB+"> AB+ </option>
              <option BloodGroup="O+"> O+ </option>
              <option BloodGroup="A-"> A- </option>
              <option BloodGroup="B-"> B- </option>
              <option BloodGroup="AB-"> AB- </option>  
              <option BloodGroup="O-"> O- </option>
              <input placeholder="Update Stock" name="lastName" className="form-control" 
                                            value={this.state.lastName} onChange={this.changeLastNameHandler}/>
              
           </select> */}
           </div>
        

                                {/* <h2> A+</h2>
                                    <button onClick={this.IncrementItem}>+</button>
                                    <input className="inputne" value={this.state.quantityAPostive} onChange={this.UpdateValue} />
                                    <button onClick={this.DecreaseItem}>-</button>
                                    <br></br>
                                    
                                    B+<button onClick={this.IncrementItem}>+</button>
                                    <input className="inputne" value={this.state.quantityAPostive} onChange={this.UpdateValue} />
                                    <button onClick={this.DecreaseItem}>-</button>
                                    <br></br>
                                    
                                   AB+ <button onClick={this.IncrementItem}>+</button>
                                    <input className="inputne" value={this.state.quantityAPostive} onChange={this.UpdateValue} />
                                    <button onClick={this.DecreaseItem}>-</button> */}
                            </form>
                        </div>
                </div>
            </div>   
            

                    </div>
        </div>)

      }
}
export default Stock
