import React, { Component } from "react";
import { NavLink } from "react-router-dom";
// import axios from 'axios'

 import LoginService  from "../services/LoginService";


 class SignUp extends Component {
    constructor(props) {
        super(props)

        this.state = {
            userName: '',
            role: '',
            password: '',
            emailId: '',
            contactNumber: '',
            fields: {},
            errors: {},
            BarStyling3: {background:"#BF222B", color:"white",padding:"0.5rem" }

        }
        this.changeuserNameHandler = this.changeuserNameHandler.bind(this);
        this.changeroleHandler = this.changeroleHandler.bind(this);
        this.changepasswordHandler = this.changepasswordHandler.bind(this);
        this.changeemailHandler = this.changeemailHandler.bind(this);
        this.changecontactNumberHandler = this.changecontactNumberHandler.bind(this);
        this.saveOrUpdatesignUp = this.saveOrUpdatesignUp.bind(this);

        
    }
    saveOrUpdatesignUp = (e) => {
        e.preventDefault();
        let 
            donor = {userName: this.state.userName, 
            role: this.state.role, 
            password: this.state.password,
            emailId: this.state.emailId,
            contactNumber: this.state.contactNumber};

            console.log('donor => ' + JSON.stringify(donor));
           
        //     AdminService.createDonor(donor).then(res =>{
        //     this.props.history.push('/Login');
        //   });
            
            
    }
    changeuserNameHandler= (event) => {
        this.setState({userName: event.target.value});
        if(event.target.name==='userName'){
            if(event.target.value==='' || event.target.value===null  ){
              this.setState({
                userNameError:true
              })
            } 
           
            else {
              this.setState({
                userNameError:false,     
                userName:event.target.value
              })
            }
          }
    }
    changeroleHandler= (event) => {
        this.setState({role: event.target.value});
        if(event.target.name==='role'){
            if(event.target.value==='' || event.target.value===null  ){
              this.setState({
                roleError:true
              })
            } 
           
            else {
              this.setState({
                roleError:false,     
                role:event.target.value
              })
            }
          }
    }
    changepasswordHandler= (event) => {
        this.setState({password: event.target.value});
        if(event.target.name==='password'){
            if(event.target.value==='' || event.target.value===null  ){
              this.setState({
                passwordError:true
              })
            } 
           
            else {
              this.setState({
                passwordError:false,     
                password:event.target.value
              })
            }
          }
    }


    
 }
 export default SignUp
